package com.Veli;

public class FloatDoublePrecision {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intPI = 22/7;
		float floatPI = 22f/7;
		double doublePI = 22d/7;
		System.out.println("Integer Deðeri: " + intPI +" " +"Float Deðeri: " + floatPI +" "  + "Double Degeri: " + doublePI);
		
		double poundDegeri = 20.0;
		double donusumdegeri = 0.45359237;
		double kilodegeri = poundDegeri * donusumdegeri;
		System.out.println("Kilo Degeri : " + kilodegeri);
		
	}

}
